mapper.select:
/** com.easycompany.mapper.AdBoardMapper.getBoardList **/ SELECT BOARD_IDX, CASE BOARD_TYPE WHEN 01 THEN '공지' WHEN 02 THEN '자료실' WHEN 03 THEN 'FAQ' WHEN 04 THEN '참여마당' WHEN 05 THEN '강사공지' WHEN 06 THEN '강사자료실' END AS BOARD_TYPE, TITLE, LINK_URL, FILE_ID, VIEW_CNT, CAST( DATE_FORMAT( REG_DT, '%Y-%m-%d %H:%i:%s' ) AS CHAR(19) ) AS REG_DT, REG_ID, REG_NM, (SELECT COUNT(*) FROM edu_file WHERE FILE_KEY = BOARD_IDX AND FILE_GUBUN = 'board') AS FIEL_CNT, CONTENTS
, CATEGORY1_KEY, CATEGORY2_KEY, CATEGORY3_KEY, CATEGORY1_NAME, CATEGORY2_NAME, CATEGORY3_NAME, BOARD_REFER_TYPE
FROM edu_board
ORDER BY BOARD_IDX DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

	/**  com.easycompany.mapper.AdBoardMapper.selectDetailBoard **/	
	SELECT BOARD_IDX, BOARD_TYPE, TITLE, CONTENTS, LINK_URL, FILE_ID, VIEW_CNT, DATE_FORMAT( REG_DT, '%Y-%m-%d' ) REG_DT, REG_ID, REG_NM, CHG_DT, CHG_ID
	       ,EDU_TYPE_01, EDU_TYPE_02, EDU_TYPE_03, EDU_TYPE_04, EDU_TYPE_05, EDU_TYPE_06, EDU_TYPE_07, EDU_TYPE_08, EDU_TYPE_09, EDU_TYPE_10, EDU_TYPE_11

      	, CATEGORY1_KEY, CATEGORY2_KEY, CATEGORY3_KEY, CATEGORY1_NAME, CATEGORY2_NAME, CATEGORY3_NAME, NOTICE_YN, BOARD_REFER_TYPE


	    , NOTICE_YN

      FROM edu_board

    	BOARD_IDX = #{board_idx}
    	AND USE_YN = 'Y'

      	AND BOARD_TYPE = #{board_type}

mapper.insert:
	/**  com.easycompany.mapper.AdBoardMapper.insertBoard **/	
	INSERT 
	INTO edu_board
	(
	  	  BOARD_TYPE
	  	, TITLE
	  	, CONTENTS
	  	, REG_ID
	  	, REG_NM

      	, CATEGORY1_KEY
      	, CATEGORY2_KEY
      	, CATEGORY3_KEY
      	, CATEGORY1_NAME
      	, CATEGORY2_NAME
      	, CATEGORY3_NAME
      	, NOTICE_YN
      	, BOARD_REFER_TYPE


      , NOTICE_YN


      , EDU_TYPE_01
	  , EDU_TYPE_02
	  , EDU_TYPE_03
	  , EDU_TYPE_04
	  , EDU_TYPE_05
	  , EDU_TYPE_06
	  , EDU_TYPE_07
	  , EDU_TYPE_08
	  , EDU_TYPE_09
	  , EDU_TYPE_10
	  , EDU_TYPE_11


	) 
	VALUES 
	(
		  #{board_type}
		, #{title}
		, #{contents}
		, IFNULL(#{user_id}, 'SYSTEM')
        , IFNULL(#{user_nm}, '교육관리자')

		, #{category1_key}
		, #{category2_key}
		, #{category3_key}
		, #{category1_name}
		, #{category2_name}
		, #{category3_name}
		, #{notice_yn}
		, #{board_refer_type}


	    , #{notice_yn}


        , #{edu_type_01}
		, #{edu_type_02}
		, #{edu_type_03}
		, #{edu_type_04}
		, #{edu_type_05}
		, #{edu_type_06}
		, #{edu_type_07}
		, #{edu_type_08}
		, #{edu_type_09}
		, #{edu_type_10}
