/** com.easycompany.mapper.EduMapper.eduInfoScheduleList **/ WITH TEMP AS ( SELECT a.CATEGORY1_KEY ,(SELECT b.CATEGORY1_NAME FROM EDU_CATEGORY1 b WHERE b.CATEGORY1_KEY = a.CATEGORY1_KEY ) CATEGORY1_NAME ,a.CATEGORY2_KEY ,(SELECT b.CATEGORY2_NAME FROM EDU_CATEGORY2 b WHERE b.CATEGORY1_KEY = a.CATEGORY1_KEY AND b.CATEGORY2_KEY = a.CATEGORY2_KEY ) CATEGORY2_NAME , a.CATEGORY3_KEY ,(SELECT b.CATEGORY3_NAME FROM EDU_CATEGORY3 b WHERE b.CATEGORY1_KEY = a.CATEGORY1_KEY AND b.CATEGORY2_KEY = a.CATEGORY2_KEY AND b.CATEGORY3_KEY = a.CATEGORY3_KEY ) CATEGORY3_NAME , EDU_NAME , EDU_START_DATE , EDU_END_DATE , EDU_ORG_NAME , EDU_TARGET , EDU_NUMBER , EDU_TEAC_NAME , EDU_KEY , SCHEDULE_NO FROM EDU_SCHEDULE A ) SELECT * FROM TEMP WHERE 1= 1
AND CATEGORY3_NAME LIKE CONCAT('%', #{category3_name} ,'%')
AND EDU_START_DATE = DATE_FORMAT(NOW(),'%x-%m-%d')
AND EDU_START_DATE >= #{edu_start_date} AND EDU_END_DATE <= #{edu_end_date}
ORDER BY SCHEDULE_NO DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

/** com.easycompany.mapper.EduMapper.eduInfoScheduleCount **/
SELECT COUNT(*) cnt
FROM EDU_SCHEDULE a

mapper.insert:
	/** com.easycompany.mapper.EduMapper.insertBasket **/

		SELECT IFNULL(MAX(BASKET_NO), 0) + 1
		FROM EDU_BASKET


	INSERT
	INTO EDU_BASKET
	( BASKET_NO
	, EDU_NO

    , SCHEDULE_NO

	, USER_ID
	, USER_NM
	, REG_ID
	)
	VALUES
	(
	  #{basket_no}
	, #{edu_no}

	, #{schedule_no}

	, #{user_id}
	, #{user_nm}
	, IFNULL(#{user_id},'SYSTEM')
	)

	/** com.easycompany.mapper.EduMapper.insertCourse **/

		SELECT IFNULL(MAX(COUR_NO), 0) + 1
		FROM EDU_COURSE_REGISTRATION


	INSERT
	INTO EDU_COURSE_REGISTRATION
	( COUR_NO
	, EDU_NO

    , SCHEDULE_NO

	, USER_ID
	, USER_NM
	, EML_ADDR
	, MBL_TELNO
	, REG_ID
	)
	VALUES
	(
	  #{cour_no}
	, #{edu_no}

	, #{schedule_no}

	, #{user_id}
	, #{user_nm}
	, #{eml_addr}
	, #{mbl_telno}
	, IFNULL(#{user_id},'SYSTEM')
	)

mapper.update:
	/** com.easycompany.mapper.EduMapper.updateCourseStatus **/
	UPDATE EDU_REGISTRATION
  	   SET EDU_STATUS = #{edu_status}
		 , CHG_ID = IFNULL(#{user_id},'SYSTEM')
	     , CHG_DT = NOW()
	  WHERE EDU_NO = #{edu_no}

mapper.select:
/** com.easycompany.mapper.EduMapper.getLifeEduOrgList **/ WITH CLASS AS ( SELECT A.CATEGORY1_KEY, B.CATEGORY2_KEY,C.CATEGORY3_KEY, A.CATEGORY1_NAME, B.CATEGORY2_NAME,C.CATEGORY3_NAME,A.SITE FROM EDU_CLASS_CATEGORY1 AS A LEFT OUTER JOIN EDU_CLASS_CATEGORY2 AS B ON(A.CATEGORY1_KEY = B.CATEGORY1_KEY AND A.SITE = B.SITE) LEFT OUTER JOIN EDU_CLASS_CATEGORY3 AS C ON (B.CATEGORY2_KEY = C.CATEGORY2_KEY AND C.SITE = B.SITE) ), REGI_D AS ( SELECT A.EDU_NO, A.EDU_SITE, A.CATEGORY1_KEY, A.CATEGORY2_KEY,A.CATEGORY3_KEY, B.CATEGORY1_NAME , B.CATEGORY2_NAME,B.CATEGORY3_NAME , A.INST_NM, A.TRAIN_S_DATE, A.TRAIN_E_DATE, A.EDU_TARGET,A.EDU_TIME , A.EDU_GARDEN, A.EDU_STATUS,A.EDU_CONT,A.EDU_METHOD, A.EXP_USE_YN , A.COPER_NM , A.COPER_NM_AUTO, A.COPER_ID, A.COPER_CHECK,A.REG_DT FROM EDU_REGISTRATION A, CLASS B WHERE A.CATEGORY1_KEY = B.CATEGORY1_KEY AND A.CATEGORY2_KEY = B.CATEGORY2_KEY AND A.CATEGORY3_KEY = B.CATEGORY3_KEY AND A.CATEGORY1_KEY = #{category1_key} AND A.CATEGORY2_KEY = #{category2_key} AND A.EXP_USE_YN ='Y' AND A.EDU_STATUS =#{edu_status} AND A.EDU_SITE =#{edu_site} ) SELECT * FROM REGI_D WHERE 1=1
AND INST_NM LIKE CONCAT('%', #{searchKeyword} ,'%')
AND CATEGORY3_NAME LIKE CONCAT('%', #{searchKeyword} ,'%')
AND TRAIN_S_DATE = DATE_FORMAT(NOW(),'%x-%m-%d')
AND TRAIN_S_DATE >= #{train_s_date} AND TRAIN_E_DATE <= #{train_e_date}
ORDER BY TRAIN_S_DATE DESC
ORDER BY REG_DT DESC
LIMIT #{recordCountPerPage} OFFSET #{offset}

/** com.easycompany.mapper.EduMapper.getLifeEduOrgCount **/ WITH CLASS AS ( SELECT A.CATEGORY1_KEY, B.CATEGORY2_KEY,C.CATEGORY3_KEY, A.CATEGORY1_NAME, B.CATEGORY2_NAME,C.CATEGORY3_NAME,A.SITE FROM EDU_CLASS_CATEGORY1 AS A LEFT OUTER JOIN EDU_CLASS_CATEGORY2 AS B ON(A.CATEGORY1_KEY = B.CATEGORY1_KEY AND A.SITE = B.SITE) LEFT OUTER JOIN EDU_CLASS_CATEGORY3 AS C ON (B.CATEGORY2_KEY = C.CATEGORY2_KEY AND C.SITE = B.SITE) ), REGI_D AS ( SELECT A.EDU_SITE, A.CATEGORY1_KEY, A.CATEGORY2_KEY,A.CATEGORY3_KEY, B.CATEGORY1_NAME , B.CATEGORY2_NAME,B.CATEGORY3_NAME , A.INST_NM, A.TRAIN_S_DATE, A.TRAIN_E_DATE, A.EDU_TARGET,A.EDU_TIME , A.EDU_GARDEN, A.EDU_STATUS,A.EDU_CONT,A.EDU_METHOD, A.EXP_USE_YN , A.COPER_NM , A.COPER_NM_AUTO, A.COPER_ID, A.COPER_CHECK,A.REG_DT FROM EDU_REGISTRATION A, CLASS B WHERE A.CATEGORY1_KEY = B.CATEGORY1_KEY AND A.CATEGORY2_KEY = B.CATEGORY2_KEY AND A.CATEGORY3_KEY = B.CATEGORY3_KEY AND A.EXP_USE_YN ='Y' AND A.CATEGORY1_KEY = #{category1_key} AND A.CATEGORY2_KEY = #{category2_key} AND A.EDU_STATUS =#{edu_status} AND A.EDU_SITE =#{edu_site} ) SELECT count(*) FROM REGI_D WHERE 1=1
AND INST_NM LIKE CONCAT('%', #{searchKeyword} ,'%')
AND CATEGORY3_NAME LIKE CONCAT('%', #{searchKeyword} ,'%')
AND TRAIN_S_DATE = DATE_FORMAT(NOW(),'%x-%m-%d')
AND TRAIN_S_DATE >= #{train_s_date} AND TRAIN_E_DATE <= #{train_e_date}
