SELECT BOARD_IDX, CASE BOARD_TYPE WHEN 05 THEN '강사공지' WHEN 06 THEN '강사자료실' END AS BOARD_TYPE, TITLE, LINK_URL, FILE_ID, VIEW_CNT, CAST( DATE_FORMAT( REG_DT, '%Y-%m-%d' ) AS CHAR(19) ) AS REG_DT, REG_ID, REG_NM, (SELECT COUNT(*) FROM edu_file WHERE FILE_KEY = BOARD_IDX AND FILE_GUBUN = 'board') AS FIEL_CNT, CONTENTS FROM edu_board WHERE USE_YN = 'Y'
AND (TITLE LIKE CONCAT('%', #{searchKeyword}, '%') OR REG_ID LIKE CONCAT('%', #{searchKeyword}, '%') OR CONTENTS LIKE CONCAT('%', #{searchKeyword}, '%'))
AND TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
AND REG_ID LIKE CONCAT('%', #{searchKeyword}, '%')
AND CONTENTS LIKE CONCAT('%', #{searchKeyword}, '%')
AND CAST( DATE_FORMAT( REG_DT, '%Y-%m-%d' ) AS CHAR(19)) >= #{board_start_date} AND CAST( DATE_FORMAT( REG_DT, '%Y-%m-%d' ) AS CHAR(19)) <= #{board_end_date}
AND BOARD_TYPE = #{board_type}
ORDER BY BOARD_IDX DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

