SELECT * FROM ( SELECT c.USER_NM, c.USER_ID, (SELECT CD_NM FROM COM_CD_MNG WHERE UP_CD_NO = '32' AND CD = c.AREA_CD) AS AREA_NM, (SELECT LICENSE_NUMBER FROM EDU_USER_LICENSE WHERE CATEGORY1_KEY = '3' AND CATEGORY2_KEY = '7' AND USER_ID = c.USER_ID) AS LICENSE_NUMBER, c.INSTRUCTOR_COPER_NM, CASE WHEN a.REASON = '1' THEN '출산 및 육아' WHEN a.REASON = '2' THEN '병가' WHEN a.REASON = '3' THEN '해외연수' WHEN a.REASON = '4' THEN '기타' END AS REASON, c.JUSO, c.MBL_TELNO, c.EML_ADDR, (SELECT FILE_NAME FROM EDU_FILE WHERE FILE_GUBUN = 'insLeave' AND FILE_KEY = a.INSTRUCTOR_LEAVE_IDX) AS FILE_NAME, (SELECT FILE_ID FROM EDU_FILE WHERE FILE_GUBUN = 'insLeave' AND FILE_KEY = a.INSTRUCTOR_LEAVE_IDX) AS FILE_ID, CASE WHEN a.STATUS = '1' THEN '승인' ELSE '미승인' END AS INS_STATUS, a.INSTRUCTOR_LEAVE_IDX FROM EDU_INSTRUCTOR_LEAVE a, EDU_INSTRUCTOR b, COM_USER c WHERE a.INSTRUCTOR_IDX = b.INSTRUCTOR_IDX AND b.COM_USER_ID = c.USER_ID
AND a.REASON = #{reason}
AND a.S_DATE <= NOW() AND a.E_DATE >= NOW()
AND a.S_DATE >= #{start_date} AND a.E_DATE <= #{end_date}
) tb WHERE 1=1
AND tb.USER_NM LIKE CONCAT('%',#{ins_nm},'%')
AND tb.AREA_NM LIKE CONCAT('%',#{area_nm},'%')
LIMIT #{recordCountPerPage} OFFSET #{offset}

