SELECT (SELECT CATEGORY1_NAME FROM EDU_CLASS_CATEGORY1 WHERE CATEGORY1_KEY = b.CATEGORY1_KEY) AS CATEGORY1_NAME ,(SELECT CATEGORY2_NAME FROM EDU_CLASS_CATEGORY2 WHERE CATEGORY2_KEY = b.CATEGORY2_KEY AND CATEGORY1_KEY = b.CATEGORY1_KEY) AS CATEGORY2_NAME ,(SELECT CATEGORY3_NAME FROM EDU_CLASS_CATEGORY3 WHERE CATEGORY3_KEY = b.CATEGORY3_KEY AND CATEGORY2_KEY = b.CATEGORY2_KEY AND CATEGORY1_KEY = b.CATEGORY1_KEY) AS CATEGORY3_NAME ,DATE_FORMAT(a.REG_DT,'%Y-%m-%d') AS REG_DT, b.INST_NM, b.EDU_TIME ,(SELECT TIME_FORMAT(SEC_TO_TIME(SUM(c.COUR_TIME)), '%H시간 %i분 %s초') FROM EDU_COURSE_ONLINE c WHERE c.COUR_NO = a.COUR_NO AND c.USER_ID = a.USER_ID) AS COUR_TIME ,a.USER_NM, a.COUR_NO, b.EDU_NO ,CASE a.COUR_STAT WHEN 1 THEN '완료' ELSE '미완료' END AS COUR_STAT FROM EDU_COURSE_REGISTRATION a, EDU_REGISTRATION b WHERE a.EDU_NO = b.EDU_NO AND b.CATEGORY1_KEY IN ('1','2','3') AND a.USER_ID = #{UserAccount.user_id}
AND IFNULL(TRAIN_S_DATE,(SELECT EDU_START_DATE FROM EDU_SCHEDULE WHERE SCHEDULE_NO = a.SCHEDULE_NO)) <= DATE_FORMAT(NOW(),'%Y-%m-%d')
AND (a.COUR_STAT != '1' OR a.COUR_STAT IS NULL)
AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d')
AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') >= #{start_date} AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') <= #{end_date}
