SELECT (SELECT CATEGORY1_NAME FROM EDU_CLASS_CATEGORY1 WHERE CATEGORY1_KEY = b.CATEGORY1_KEY) AS CATEGORY1_NAME ,(SELECT CATEGORY2_NAME FROM EDU_CLASS_CATEGORY2 WHERE CATEGORY2_KEY = b.CATEGORY2_KEY AND CATEGORY1_KEY = b.CATEGORY1_KEY) AS CATEGORY2_NAME ,(SELECT CATEGORY3_NAME FROM EDU_CLASS_CATEGORY3 WHERE CATEGORY3_KEY = b.CATEGORY3_KEY AND CATEGORY2_KEY = b.CATEGORY2_KEY AND CATEGORY1_KEY = b.CATEGORY1_KEY) AS CATEGORY3_NAME ,DATE_FORMAT(a.REG_DT,'%Y-%m-%d') AS REG_DT, b.INST_NM ,a.USER_NM, a.COUR_NO, a.EDU_NO ,DATE_FORMAT(a.LICENSE_PBL_DATE,'%Y-%m-%d') AS LICENSE_PBL_DATE ,CASE a.LICENSE_PBL WHEN 1 THEN '발급' ELSE '미발급' END AS LICENSE_PBL FROM EDU_COURSE_REGISTRATION a, EDU_REGISTRATION b WHERE a.EDU_NO = b.EDU_NO AND a.USER_ID = #{UserAccount.user_id}
AND IFNULL(TRAIN_S_DATE,(SELECT EDU_START_DATE FROM EDU_SCHEDULE WHERE SCHEDULE_NO = a.SCHEDULE_NO)) <= DATE_FORMAT(NOW(),'%Y-%m-%d')
AND COUR_STAT = 1 AND b.CATEGORY1_KEY = #{category1_key}
AND b.CATEGORY2_KEY = #{category2_key}
AND b.CATEGORY3_KEY = #{category3_key}
AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d')
