SELECT a.SCHEDULE_NO, a.EDU_START_DATE AS EDU_DATE, CONCAT(SUBSTR(TIME(b.START_TIME),1,2),':',SUBSTR(TIME(b.START_TIME),4,2),'~',SUBSTR(TIME(b.END_TIME),1,2),':',SUBSTR(TIME(b.END_TIME),4,2)) AS EDU_DATETIME, (SELECT CD_NM FROM COM_CD_MNG WHERE UP_CD_NO = '32' AND CD = b.COPER_AREA_CD) AS AREA_NM, b.EDU_PLACE, a.EDU_ORG_NAME, a.EDU_NAME, a.EDU_TARGET, a.EDU_NUMBER, c.REG_DT, CASE WHEN b.INS_STATUS IS NULL THEN '취소하기' ELSE '변경불가' END AS INS_STATUS, CASE WHEN c.INS_CONFIRM = 'Y' THEN '선택' ELSE '미선택' END AS INS_CONFIRM, a.EDU_KEY FROM EDU_SCHEDULE a, EDU_SCHEDULE_DETAIL b, EDU_SCHEDULE_APP c WHERE a.SCHEDULE_NO = b.SCHEDULE_NO AND a.SCHEDULE_NO = c.SCHEDULE_NO AND c.INS_USER = #{UserAccount.user_id}
AND EDU_START_DATE = DATE_FORMAT(NOW(),'%Y-%m-%d')
AND EDU_START_DATE >= #{start_date} AND EDU_START_DATE <= #{end_date}
AND a.EDU_NAME LIKE CONCAT('%',#{edu_nm},'%')
AND b.COPER_AREA_CD = #{area_nm}
AND a.EDU_ORG_NAME LIKE CONCAT('%',#{edu_org_name},'%')
ORDER BY a.SCHEDULE_NO DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

