AND LICENSE_PBL = '1'
AND LICENSE_PBL = '2'

SELECT (SELECT CATEGORY1_NAME FROM EDU_CLASS_CATEGORY1 WHERE CATEGORY1_KEY = a.CATEGORY1_KEY) AS CATEGORY1_NAME, (SELECT CATEGORY2_NAME FROM EDU_CLASS_CATEGORY2 WHERE CATEGORY2_KEY = a.CATEGORY2_KEY AND CATEGORY1_KEY = a.CATEGORY1_KEY) AS CATEGORY2_NAME, (SELECT CATEGORY3_NAME FROM EDU_CLASS_CATEGORY3 WHERE CATEGORY3_KEY = a.CATEGORY3_KEY AND CATEGORY2_KEY = a.CATEGORY2_KEY AND CATEGORY1_KEY = a.CATEGORY1_KEY) AS CATEGORY3_NAME, a.SCHEDULE_NO, a.EDU_TARGET, a.EDU_START_DATE, b.EDU_PLACE, b.INS_TYPE, a.EDU_TEAC_ID, CASE WHEN b.INS_TYPE = 'recruit' AND a.EDU_TEAC_ID IS NULL THEN 'Y' ELSE 'N' END AS CANCEL_YN FROM EDU_SCHEDULE a, EDU_SCHEDULE_DETAIL b WHERE 1=1 AND a.SCHEDULE_NO = b.SCHEDULE_NO AND a.REG_ID = #{UserAccount.user_id} AND a.CATEGORY1_KEY = '7'
AND a.CATEGORY2_KEY = #{category2_key}
AND a.CATEGORY3_KEY = #{category3_key}
AND a.EDU_START_DATE = DATE_FORMAT(NOW(),'%Y-%m-%d')
AND a.EDU_START_DATE >= #{start_date} AND a.EDU_START_DATE <= #{end_date}
ORDER BY a.SCHEDULE_NO DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

SELECT COUNT(*) 
FROM EDU_SCHEDULE a,
	 EDU_SCHEDULE_DETAIL b
WHERE 1=1
AND a.SCHEDULE_NO = b.SCHEDULE_NO
AND a.REG_ID = #{UserAccount.user_id}
AND a.CATEGORY1_KEY = '7'

	AND a.CATEGORY2_KEY = #{category2_key}


	AND a.CATEGORY3_KEY = #{category3_key}





		AND a.EDU_START_DATE = DATE_FORMAT(NOW(),'%Y-%m-%d')



       AND  a.EDU_START_DATE >= #{start_date} AND a.EDU_START_DATE <= #{end_date}

SELECT a.SCHEDULE_NO, a.EDU_NAME, a.EDU_START_DATE, (SELECT EDU_TIME FROM EDU_REGISTRATION WHERE EDU_NO = a.EDU_KEY) AS EDU_TIME, a.EDU_ORG_NAME, (SELECT FN_DECRYPT(ENCPT_USER_NM) FROM COM_USER WHERE USER_ID = a.REG_ID) AS USER_NM, (SELECT CD_NM FROM COM_CD_MNG WHERE UP_CD_NO = '32' AND CD = b.COPER_AREA_CD) AS AREA_NM, a.EDU_TARGET, CASE WHEN a.EDU_TEAC_ID IS NOT NULL THEN (SELECT FN_DECRYPT(ENCPT_USER_NM) FROM COM_USER WHERE USER_ID = a.EDU_TEAC_ID) WHEN a.EDU_TEAC_ID IS NULL THEN a.EDU_TEAC_NAME END AS EDU_TEAC_NM, a.EDU_TEAC_ID, CASE WHEN b.INS_STATUS IS NOT NULL THEN '1' ELSE '2' END AS INS_STATUS FROM EDU_SCHEDULE a, EDU_SCHEDULE_DETAIL b
WHERE 1=1 AND a.SCHEDULE_NO = b.SCHEDULE_NO AND a.REG_ID = #{UserAccount.user_id} AND b.INS_STATUS IS NULL AND a.CATEGORY1_KEY = '7' AND b.INS_TYPE = 'recruit'
AND a.EDU_NAME LIKE CONCAT('%',#{edu_nm},'%')
AND a.EDU_START_DATE = DATE_FORMAT(NOW(),'%Y-%m-%d')
AND a.EDU_START_DATE >= #{start_date} AND a.EDU_START_DATE <= #{end_date}
ORDER BY a.SCHEDULE_NO DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

SELECT a.SCHEDULE_NO, a.EDU_NAME, a.EDU_TARGET, a.EDU_START_DATE, b.EDU_PLACE, (SELECT EDU_TIME FROM EDU_REGISTRATION WHERE EDU_NO = a.EDU_KEY) AS EDU_TIME, (SELECT COUNT(*) FROM EDU_COURSE_REGISTRATION WHERE SCHEDULE_NO = a.SCHEDULE_NO) AS APP_CNT FROM EDU_SCHEDULE a, EDU_SCHEDULE_DETAIL b
WHERE 1=1 AND a.SCHEDULE_NO = b.SCHEDULE_NO AND a.REG_ID = #{UserAccount.user_id} AND a.CATEGORY1_KEY = '7'
AND a.EDU_NAME LIKE CONCAT('%',#{edu_nm},'%')
AND a.EDU_START_DATE = DATE_FORMAT(NOW(),'%Y-%m-%d')
AND a.EDU_START_DATE >= #{start_date} AND a.EDU_START_DATE <= #{end_date}
ORDER BY a.SCHEDULE_NO DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

SELECT FN_DECRYPT(b.ENCPT_USER_NM) AS USER_NM, CASE WHEN FN_DECRYPT(b.ENCPT_SEXDSTN_SE) = 'M' THEN '남' ELSE '여' END AS USER_SEX, FN_GET_CD_NM1('238', b.JOB_CD) AS JOB_CD, b.BRDT AS BIRTH_YMD, b.EML_ADDR, FN_DECRYPT(b.ENCPT_MBL_TELNO) AS MBL_TELNO, FN_DECRYPT(b.ENCPT_ADDR) AS JUSO, FN_DECRYPT(b.ENCPT_DADDR) AS JUSO_DETAIL, DATE_FORMAT(a.REG_DT,'%Y-%m-%d') AS REG_DT FROM EDU_COURSE_REGISTRATION a, COM_USER b WHERE 1=1 AND a.USER_ID = b.USER_ID AND a.SCHEDULE_NO = #{sch_no}
AND FN_DECRYPT(b.ENCPT_USER_NM) LIKE CONCAT('%',#{user_nm},'%')
AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d')
AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') >= #{start_date} AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') <= #{end_date}
ORDER BY REG_DT DESC
ORDER BY FN_DECRYPT(b.ENCPT_USER_NM) ASC
ORDER BY a.COUR_NO DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

SELECT COUNT(*) FROM EDU_COURSE_REGISTRATION a, COM_USER b WHERE 1=1 AND a.USER_ID = b.USER_ID AND a.SCHEDULE_NO = #{sch_no}
AND FN_DECRYPT(b.ENCPT_USER_NM) LIKE CONCAT('%',#{user_nm},'%')
AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d')
AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') >= #{start_date} AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') <= #{end_date}

SELECT a.SCHEDULE_NO, a.EDU_NAME, a.EDU_TARGET, a.EDU_START_DATE, b.EDU_PLACE, (SELECT EDU_TIME FROM EDU_REGISTRATION WHERE EDU_NO = a.EDU_KEY) AS EDU_TIME, CASE WHEN b.SCH_STATUS = 'S' THEN '완료' ELSE '미완료' END AS SCH_STATUS FROM EDU_SCHEDULE a, EDU_SCHEDULE_DETAIL b
WHERE 1=1 AND a.SCHEDULE_NO = b.SCHEDULE_NO AND a.REG_ID = #{UserAccount.user_id} AND a.CATEGORY1_KEY = '7'
AND a.EDU_NAME LIKE CONCAT('%',#{edu_nm},'%')
AND a.EDU_START_DATE = DATE_FORMAT(NOW(),'%Y-%m-%d')
AND a.EDU_START_DATE >= #{start_date} AND a.EDU_START_DATE <= #{end_date}
ORDER BY a.SCHEDULE_NO DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

SELECT FN_DECRYPT(b.ENCPT_USER_NM) AS USER_NM, CASE WHEN FN_DECRYPT(b.ENCPT_SEXDSTN_SE) = 'M' THEN '남' ELSE '여' END AS USER_SEX, FN_GET_CD_NM1('238', b.JOB_CD) AS JOB_CD, b.BRDT AS BIRTH_YMD, b.EML_ADDR, FN_DECRYPT(b.ENCPT_MBL_TELNO) AS MBL_TELNO, FN_DECRYPT(b.ENCPT_ADDR) AS JUSO, FN_DECRYPT(b.ENCPT_DADDR) AS JUSO_DETAIL, DATE_FORMAT(a.REG_DT,'%Y-%m-%d') AS REG_DT, a.COUR_NO, a.EDU_NO FROM EDU_COURSE_REGISTRATION a, COM_USER b WHERE 1=1 AND a.USER_ID = b.USER_ID AND a.SCHEDULE_NO = #{sch_no}
AND FN_DECRYPT(b.ENCPT_USER_NM) LIKE CONCAT('%',#{user_nm},'%')
ORDER BY REG_DT DESC
ORDER BY FN_DECRYPT(b.ENCPT_USER_NM) ASC
ORDER BY a.COUR_NO DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

SELECT a.SCHEDULE_NO ,(SELECT CATEGORY1_NAME FROM EDU_CLASS_CATEGORY1 WHERE CATEGORY1_KEY = a.CATEGORY1_KEY) AS CATEGORY1_NAME ,(SELECT CATEGORY2_NAME FROM EDU_CLASS_CATEGORY2 WHERE CATEGORY2_KEY = a.CATEGORY2_KEY AND CATEGORY1_KEY = a.CATEGORY1_KEY) AS CATEGORY2_NAME ,(SELECT CATEGORY3_NAME FROM EDU_CLASS_CATEGORY3 WHERE CATEGORY3_KEY = a.CATEGORY3_KEY AND CATEGORY2_KEY = a.CATEGORY2_KEY AND CATEGORY1_KEY = a.CATEGORY1_KEY) AS CATEGORY3_NAME ,DATE_FORMAT(a.REG_DT,'%Y-%m-%d') AS REG_DT ,CASE WHEN b.SCH_STATUS = 'S' THEN '완료' ELSE '미완료' END AS SCH_STATUS FROM EDU_SCHEDULE a, EDU_SCHEDULE_DETAIL b
WHERE 1=1 AND a.SCHEDULE_NO = b.SCHEDULE_NO AND a.REG_ID = #{UserAccount.user_id}
AND b.SCH_STATUS = 'S'
AND a.CATEGORY3_KEY = #{category3_key}
ORDER BY a.SCHEDULE_NO DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

