mapper.insert:

INSERT INTO EDU_SCHEDULE(
  	  CATEGORY1_KEY
  	, CATEGORY2_KEY
  	, CATEGORY3_KEY
  	, EDU_NAME
  	, EDU_START_DATE
  	, EDU_ORG_NAME
  	, EDU_TARGET
  	, EDU_NUMBER
  	, EDU_TEAC_NAME
  	, EDU_TEAC_ID
  	, EDU_KEY
  	, REG_DT
  	, REG_ID
) VALUES (
	  #{category1_key}
	, #{category2_key}
	, #{category3_key}
	, #{edu_nm}
	, #{edu_date}
	, #{coper_nm}
	, #{edu_target}
	, #{edu_garden}
	, IFNULL(#{user_nm},#{ins_input})
	, #{user_id}
	, #{edu_no}
	, DATE_FORMAT(NOW(),'%x-%m-%d')
	, #{UserAccount.user_id}
)

mapper.select:
	SELECT  SCHEDULE_NO
		FROM EDU_SCHEDULE
		 WHERE 1=1
		AND EDU_KEY = ${edu_no}
		AND REG_DT = DATE_FORMAT(NOW(),'%x-%m-%d')
        AND REG_ID = #{UserAccount.user_id}

mapper.insert:

INSERT INTO EDU_SCHEDULE_DETAIL(
  	  SCHEDULE_NO
  	, COPER_AREA_CD
  	, COPER_ADDR
  	, USER_NM
  	, USER_TELNO
  	, USER_EML
  	, INS_TYPE
  	, START_TIME
  	, END_TIME
  	, EDU_PLACE
  	, REG_DT
  	, REG_ID
) VALUES (
	  #{schedule_no}
	, #{area_cd}
	, #{coper_addr}
	, #{app_user_nm}
	, #{app_tel}
	, CONCAT(#{app_email1},'@',#{app_email2})
	, #{ins_type}
	, CONCAT(#{start_hour},':',#{start_min})
	, CONCAT(#{end_hour},':',#{end_min})
	, #{edu_place}
	,DATE_FORMAT(NOW(),'%x-%m-%d')
	, #{UserAccount.user_id} 
)

mapper.select:
SELECT a.SCHEDULE_NO, a.EDU_NAME, a.EDU_START_DATE AS EDU_DATE, CONCAT(SUBSTR(TIME(b.START_TIME),1,2),':',SUBSTR(TIME(b.START_TIME),4,2),'~',SUBSTR(TIME(b.END_TIME),1,2),':',SUBSTR(TIME(b.END_TIME),4,2)) AS EDU_DATETIME, (SELECT EDU_TIME FROM EDU_REGISTRATION WHERE EDU_NO = a.EDU_KEY) AS EDU_TIME, a.EDU_ORG_NAME, a.EDU_TARGET, a.EDU_TEAC_NAME, CASE WHEN b.INS_TYPE = 'recruit' AND INS_STATUS IS NULL THEN '섭외중' WHEN b.INS_TYPE = 'recruit' AND INS_STATUS IS NOT NULL THEN '완료' WHEN b.INS_TYPE = 'select' AND INS_STATUS IS NULL THEN '완료' END AS INS_STATUS,
CASE WHEN b.INS_TYPE = 'recruit' AND INS_STATUS IS NULL AND b.SCH_STATUS IS NULL AND a.EDU_START_DATE > NOW() THEN '' WHEN b.INS_TYPE = 'recruit' AND INS_STATUS IS NOT NULL AND b.SCH_STATUS IS NULL AND a.EDU_START_DATE > NOW() THEN '신청완료' WHEN b.INS_TYPE = 'select' AND INS_STATUS IS NULL AND b.SCH_STATUS IS NULL AND a.EDU_START_DATE > NOW() THEN '신청완료' WHEN b.SCH_STATUS = 'C' THEN '신청취소' WHEN b.SCH_STATUS IS NULL AND a.EDU_START_DATE <= NOW() THEN '교육완료' WHEN b.SCH_STATUS = 'S' THEN '보고완료' END AS SCH_STATUS
FROM EDU_SCHEDULE a, EDU_SCHEDULE_DETAIL b WHERE a.SCHEDULE_NO = b.SCHEDULE_NO LIMIT #{recordCountPerPage} OFFSET #{offset}

mapper.update:

UPDATE EDU_SCHEDULE
SET	EDU_TEAC_NAME = #{user_nm},
	EDU_TEAC_ID = #{user_id}
WHERE SCHEDULE_NO = #{schedule_no}


UPDATE EDU_SCHEDULE_DETAIL
SET	INS_STATUS = 'Y'
WHERE SCHEDULE_NO = #{schedule_no}


UPDATE EDU_SCHEDULE_APP
SET	INS_CONFIRM = 'Y'
WHERE SCHEDULE_NO = #{schedule_no} 		
AND INS_USER = #{user_id}


UPDATE EDU_COURSE_REGISTRATION
SET	USE_YN = 'N'
WHERE USER_ID IN (

		#{item}

)


UPDATE EDU_COURSE_REGISTRATION
SET	COUR_STAT = '1'
WHERE USER_ID IN (

		#{item}

)


UPDATE EDU_SCHEDULE_DETAIL
SET	SCH_STATUS = 'S'
WHERE SCHEDULE_NO = #{sch_no}
