	, #{category3_key}
	, #{edu_nm}
	, #{edu_date}
	, #{coper_nm}
	, #{edu_target}
	, #{edu_garden}
	, CONCAT(#{user_nm},#{ins_input})
	, #{user_id}
	, #{edu_no}
	, DATE_FORMAT(NOW(),'%x-%m-%d')
	, #{UserAccount.user_id}
)

