)

mapper.select:
SELECT a.SCHEDULE_NO, a.EDU_NAME, a.EDU_START_DATE AS EDU_DATE, CONCAT(SUBSTR(TIME(b.START_TIME),1,2),':',SUBSTR(TIME(b.START_TIME),4,2),'~',SUBSTR(TIME(b.END_TIME),1,2),':',SUBSTR(TIME(b.END_TIME),4,2)) AS EDU_DATETIME, (SELECT EDU_TIME FROM EDU_REGISTRATION WHERE EDU_NO = a.EDU_KEY) AS EDU_TIME, a.EDU_ORG_NAME, a.EDU_TARGET, a.EDU_TEAC_NAME, CASE WHEN b.INS_TYPE = 'recruit' AND INS_STATUS IS NULL THEN '섭외중' WHEN b.INS_TYPE = 'recruit' AND INS_STATUS IS NOT NULL THEN '완료' WHEN b.INS_TYPE = 'select' AND INS_STATUS IS NULL THEN '완료' WHEN b.INS_TYPE = 'input' AND INS_STATUS IS NULL THEN '완료' END AS INS_STATUS,
CASE WHEN b.INS_TYPE = 'recruit' AND INS_STATUS IS NULL AND b.SCH_STATUS IS NULL AND a.EDU_START_DATE > DATE_FORMAT(NOW(),'%x-%m-%d') THEN '' WHEN b.INS_TYPE = 'recruit' AND INS_STATUS IS NOT NULL AND b.SCH_STATUS IS NULL AND a.EDU_START_DATE > DATE_FORMAT(NOW(),'%x-%m-%d') THEN '신청완료' WHEN b.INS_TYPE = 'select' AND INS_STATUS IS NULL AND b.SCH_STATUS IS NULL AND a.EDU_START_DATE > DATE_FORMAT(NOW(),'%x-%m-%d') THEN '신청완료' WHEN b.INS_TYPE = 'input' AND INS_STATUS IS NULL AND b.SCH_STATUS IS NULL AND a.EDU_START_DATE > DATE_FORMAT(NOW(),'%x-%m-%d') THEN '신청완료' WHEN b.SCH_STATUS = 'C' THEN '신청취소' WHEN b.SCH_STATUS IS NULL AND a.EDU_START_DATE <= DATE_FORMAT(NOW(),'%x-%m-%d') THEN '교육완료' WHEN b.SCH_STATUS = 'S' THEN '보고완료' END AS SCH_STATUS
FROM EDU_SCHEDULE a, EDU_SCHEDULE_DETAIL b WHERE a.SCHEDULE_NO = b.SCHEDULE_NO AND a.REG_ID = #{UserAccount.user_id} ORDER BY SCHEDULE_NO DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

mapper.update:

UPDATE EDU_SCHEDULE
SET	EDU_TEAC_NAME = #{user_nm},
	EDU_TEAC_ID = #{user_id}
WHERE SCHEDULE_NO = #{schedule_no}


UPDATE EDU_SCHEDULE_DETAIL
SET	INS_STATUS = 'Y'
WHERE SCHEDULE_NO = #{schedule_no}


UPDATE EDU_SCHEDULE_APP
SET	INS_CONFIRM = 'Y'
WHERE SCHEDULE_NO = #{schedule_no} 		
AND INS_USER = #{user_id}


UPDATE EDU_COURSE_REGISTRATION
SET	USE_YN = 'N'
WHERE USER_ID IN (

		#{item}

)


UPDATE EDU_COURSE_REGISTRATION
SET	COUR_STAT = '1'
WHERE USER_ID IN (

		#{item}

)


UPDATE EDU_SCHEDULE_DETAIL
SET	SCH_STATUS = 'S'
WHERE SCHEDULE_NO = #{sch_no}

mapper.select:
SELECT a.SCHEDULE_NO, a.EDU_NAME, a.EDU_TARGET, a.EDU_START_DATE, b.EDU_PLACE, (SELECT EDU_TIME FROM EDU_REGISTRATION WHERE EDU_NO = a.EDU_KEY) AS EDU_TIME, (SELECT COUNT(*) FROM EDU_COURSE_REGISTRATION WHERE SCHEDULE_NO = a.SCHEDULE_NO) AS APP_CNT FROM EDU_SCHEDULE a, EDU_SCHEDULE_DETAIL b
WHERE 1=1 AND a.SCHEDULE_NO = b.SCHEDULE_NO AND a.CATEGORY1_KEY = '7'
AND a.EDU_NAME LIKE CONCAT('%',#{edu_nm},'%')
AND a.EDU_START_DATE = DATE_FORMAT(NOW(),'%x-%m-%d')
AND a.EDU_START_DATE >= #{start_date} AND a.EDU_START_DATE <= #{end_date}
ORDER BY a.SCHEDULE_NO DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

mapper.update:

UPDATE EDU_SCHEDULE
SET   EDU_START_DATE = #{start_date}
  	, EDU_ORG_NAME = #{coper_nm}
  	, EDU_TARGET = #{edu_target}
  	, EDU_NUMBER = #{edu_number}
  	, EDU_TEAC_NAME = CONCAT(#{user_nm},#{ins_input})
  	, EDU_TEAC_ID = #{user_id}
WHERE SCHEDULE_NO = #{schedule_no}


UPDATE EDU_SCHEDULE_DETAIL
SET    COPER_AREA_CD = #{area_cd}
  	 , COPER_ADDR = #{coper_addr}
  	 , USER_NM = #{app_user_nm}
  	 , USER_TELNO = #{app_tel}
  	 , USER_EML = #{app_email}
  	 , INS_TYPE = #{ins_type}
  	 , START_TIME = CONCAT(#{start_hour},':',#{start_min})
  	 , END_TIME = CONCAT(#{end_hour},':',#{end_min})
  	 , EDU_PLACE = #{edu_place}
  	 , CANCEL_REASON = #{cancel_reason}
  	 , CANCEL_DATE = #{cancel_date}
  	 , CANCEL_USER = #{cancel_user}
  	 , CANCEL_TEL = #{cancel_tel}
  	 , CANCEL_REMARK = #{cancel_remark}
WHERE SCHEDULE_NO = #{schedule_no}

mapper.select:
SELECT a.SCHEDULE_NO, a.EDU_NAME, a.EDU_TARGET, a.EDU_START_DATE, b.EDU_PLACE, (SELECT EDU_TIME FROM EDU_REGISTRATION WHERE EDU_NO = a.EDU_KEY) AS EDU_TIME, (SELECT COUNT(*) FROM EDU_COURSE_REGISTRATION WHERE SCHEDULE_NO = a.SCHEDULE_NO) AS APP_CNT FROM EDU_SCHEDULE a, EDU_SCHEDULE_DETAIL b
WHERE 1=1 AND a.SCHEDULE_NO = b.SCHEDULE_NO AND a.CATEGORY1_KEY = '7'
AND a.EDU_NAME LIKE CONCAT('%',#{edu_nm},'%')
AND a.EDU_START_DATE = DATE_FORMAT(NOW(),'%x-%m-%d')
AND a.EDU_START_DATE >= #{start_date} AND a.EDU_START_DATE <= #{end_date}
ORDER BY a.SCHEDULE_NO DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

SELECT a.EDU_NO, a.COUR_NO, FN_DECRYPT(b.ENCPT_USER_NM) AS USER_NM, CASE WHEN FN_DECRYPT(b.ENCPT_SEXDSTN_SE) = 'M' THEN '남' ELSE '여' END AS USER_SEX, FN_GET_CD_NM1('238', b.JOB_CD) AS JOB_CD, b.BRDT AS BIRTH_YMD, b.EML_ADDR, FN_DECRYPT(b.ENCPT_MBL_TELNO) AS MBL_TELNO, FN_DECRYPT(b.ENCPT_ADDR) AS JUSO, FN_DECRYPT(b.ENCPT_DADDR) AS JUSO_DETAIL, DATE_FORMAT(a.REG_DT,'%Y-%m-%d') AS REG_DT FROM EDU_COURSE_REGISTRATION a, COM_USER b WHERE 1=1 AND a.USER_ID = b.USER_ID AND a.SCHEDULE_NO = #{sch_no}
AND FN_DECRYPT(b.ENCPT_USER_NM) LIKE CONCAT('%',#{user_nm},'%')
AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%x-%m-%d')
AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') >= #{start_date} AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') <= #{end_date}
ORDER BY REG_DT DESC
ORDER BY FN_DECRYPT(b.ENCPT_USER_NM) ASC
ORDER BY a.COUR_NO DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

SELECT COUNT(*) FROM EDU_COURSE_REGISTRATION a, COM_USER b WHERE 1=1 AND a.USER_ID = b.USER_ID AND a.SCHEDULE_NO = #{sch_no}
AND FN_DECRYPT(b.ENCPT_USER_NM) LIKE CONCAT('%',#{user_nm},'%')
AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%x-%m-%d')
AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') >= #{start_date} AND DATE_FORMAT(a.REG_DT,'%Y-%m-%d') <= #{end_date}
